function stamp(
	xFreq,
	xSpace,
	yFreq,
	ySpace,
	stepHue,
	num,
	xMin,
	xMax,
	yMin,
	yMax
) {
	for (let col = 0; col < num; col++) {
		for (let row = 0; row < num; row++) {
			let x = map(noise(col * xFreq + xSpace), 0, 1, xMin, xMax);
			let y = map(noise(row * yFreq + ySpace), 0, 1, yMin, yMax);

			let diameter = map(hue, 120, 180, 0.1, 1);
			fill(hue, 40, 95);
			ellipse(x, y, diameter, diameter);
		}

		hue += stepHue;
	}
}

