let myTurtle;
//initLength: the initial length
const initLength = 100;
//minLength: the minimum length
const minLength = 2;
//variation: the maximum degree that each branch turns left or right
const variation = 20;

function setup() {
  createCanvas(500, 500);
  noFill();
  stroke(255);
  background(100);
  noLoop();
  myTurtle = new Turtle();
}

function draw() {
  myTurtle.penUp();
  myTurtle.moveTo(250, 450);
  myTurtle.turnTo(-90);
  myTurtle.penDown();
  randomTree(initLength,minLength,variation);
}


function randomTree(length,min,variation) {
  let r = map(length,initLength,minLength,150,0);
  let g = map(length,initLength,minLength,75,255);

  if (length < minLength) {
    return;
  }

  stroke(150,75,0);
  if(length<minLength*2){
    stroke(r,g,0);
  }
  if(length*0.75<minLength){
    stroke(0,255,0);
  }

  myTurtle.moveForward(length);

  myTurtle.pushState();
  myTurtle.turnLeft(random(0,45));
  randomTree(length * 0.75,minLength,variation);
  myTurtle.popState();

  myTurtle.pushState();
  myTurtle.turnRight(random(0,45));
  randomTree(length * 0.75,minLength,variation);
  myTurtle.popState();
}

