// declaring the width of each shape
let w = 16;

// declaring an array with possible angles
let pieces = [60, 90, 120, 45];
let angles = [0, 45, 90, 135, 180, 225, 270, 315, 360];

function setup() {
  createCanvas(800, 800);

  background("black");
  angleMode(DEGREES);
}

function draw() {
  for (let i = w / 2; i < width; i = i + w) {
    for (let j = w / 2; j < height; j = j + w) {
      push();
      translate(i, j);

      DrawShape(0, 0, w, w);
      pop();
    }
  }

  // adding a grey overlay on the pattern below
  push();
  blendMode(MULTIPLY);
  fill("grey");
  rect(0, 0, width, height);
  pop();
}

// drawing each unit with concentric chords and with alternate fill
function DrawShape(x, y, w, h) {
  strokeCap(ROUND);

  let piece = random(pieces);
  let angle = random(angles);

  push();
  translate(x, y);
  rotate(random(angle));

  push();
  strokeWeight(random(3));

  fill("white");
  arc(0, 0, w, h, 0, 180 + piece, CHORD);

  fill("black");
  arc(0, 0, w - 5, h - 5, 0, 180 + piece, CHORD);

  fill("white");
  arc(0, 0, w - 10, h - 10, 0, 180 + piece, CHORD);

  fill("black");
  arc(0, 0, w - 15, h - 15, 0, 180 + piece, CHORD);
  pop();

  pop();
}

