function pickColorSafe(i, j) {
  let h = noise(i * noiseScale, j * noiseScale);
  let c = "#facade";

  if (h < 0.2) {
    c = safezone[6];
  } else if (h < 0.3) {
    c = safezone[5];
  } else if (h < 0.4) {
    c = safezone[4];
  } else if (h < 0.5) {
    c = safezone[3];
  } else if (h < 0.6) {
    c = safezone[2];
  } else if (h < 0.7) {
    c = safezone[1];
  } else {
    c = safezone[0];
  }
  return color(c);
}
