    if (diameter <= 5){
      fill(0,100,100)
    } else if (diameter <= 6){
      fill(30,100,100)        
    } else if (diameter <= 9){
      fill(60,100,100)     
    } else if (diameter <= 12){
      fill(90,100,100)         
    } else if (diameter <= 13){
      fill(120,100,100)         
    } else if (diameter <= 14){
      fill(150,100,100)         
    } else if (diameter <= 15){
      fill(180,100,100)         
    } else if (diameter <= 16){
      fill(230,100,100)        
    } else if (diameter <= 18){
      fill(270,100,100)         
    } else {
      fill(280,100,100)
    }

    ellipse(x, y, diameter, diameter);
  }

  noLoop();
}

