//Followed https://www.youtube.com/watch?v=f0KN7etR-d0 to make 3D noise structure and then modified for different surface textures using blendMode
let piece;
let mode;

function setup() {
  createCanvas(400,400,WEBGL);
  angleMode(DEGREES)
  n=int(random(1,6))
  noiseDetail(n)
  mode=int(random(0,4))
  console.log(mode)
}

function draw() {
  background(20);
  noStroke()

  translate(0,0,-width)
  rotateX(mouseX)
  rotateY(mouseY)
  //rotateZ(frameCount+noise(0,1))
  translate(-width/2,-height/2,-width/2)
  var piece=width/5
  var indexX=0
  for(let x=0;x<width;x+=piece){
    var indexY=0
    for(let y=0;y<height;y+=piece){
      var indexZ=0
      for(let z=0;z<width;z+=piece){
        if(mode==0){
          blendMode(LIGHTEST)
          push()
          let h=noise(indexX,indexY,indexZ)*255;
          var r=map(h,0,255,100,255)
          var g=map(h,0,255,255,20)
          var b=map(h,0,255,200,100)
          fill(r,g,b,30)
          translate(x+10,y,z)
          box(piece+10);
          pop()
        }
        else if(mode==1){
          blendMode(ADD)
          push()
          let h=noise(indexX,indexY,indexZ)*255;
          var r=map(h,0,255,100,255)
          var g=map(h,0,255,255,20)
          var b=map(h,0,255,200,100)
          fill(r,g,b,30)
          translate(x+10,y,z)
          box(piece+10);
          pop()
        }
        else if(mode==2){
          blendMode(REPLACE);
          push()
          let h=noise(indexX,indexY,indexZ)*255;
          var r=map(h,0,255,20,255)
          var g=map(h,0,255,255,20)
          var b=map(h,0,255,220,120)
          fill(r,g,b,30)
          translate(x+10,y,z)
          box(piece+10);
          pop()
        }
        else if(mode==3){
          blendMode(EXCLUSION);
          push()
          let h=noise(indexX,indexY,indexZ)*255;
          var r=map(h,0,255,100,200)
          var g=map(h,0,255,255,100)
          var b=map(h,0,255,100,150)
          fill(r,g,b,30)
          translate(x,y,z)
          box(piece+10);
          pop()
        }
        indexZ+=0.5
      }
      indexY+=0.6
    }
    indexX+=0.4
  }
}

