let amplitude_slider;

let startX = 0;
let startY = 50;
let endX = 480;
let endY = 50;
let startX2 = 0;
let startY2 = 150;
let endX2 = 480;
let endY2 = 150;
let t=0;

function setup() {
  createCanvas(500, 300);
  createP("Frequency");
  frequency_slider = createSlider(0,100,50);
  createP("Amplitude");
  amplitude_slider = createSlider(0, 100, 50);
  createP("Time Speed");
  timespeed_slider = createSlider(0,100,50);
  createP("Width");
  width_slider = createSlider(0,500,250);
  createP("Height");
  height_slider = createSlider(0,300,0);
}

function draw() {
  background(50);
  ellipseMode(CENTER);

  let amplitude = amplitude_slider.value() / 100;
  let frequency = frequency_slider.value()/5;
  let delta = timespeed_slider.value()/1000;
  startX = (width-width_slider.value())/2;
  startX2 = (width-width_slider.value())/2;
  endX = width-(width-width_slider.value())/2;
  endX2= width-(width-width_slider.value())/2;
  startY = (height-height_slider.value())/2;
  endY = (height-height_slider.value())/2;
  startY2 = height-(height-height_slider.value())/2;
  endY2= height-(height-height_slider.value())/2;
  t+=delta;

  noiseDetail(1, 0.5);

  fill("green");
  noStroke();
  beginShape();
  // study this loop. do you understand how the line of ellipses is created?
  for (i = 0; i < 1; i += 0.002) {
    let x = lerp(startX, endX, i);
    let y = lerp(startY, endY, i);

    // hint: drive offsetX and offsetY with noise() instead of random()
    let offsetX = noise(i*frequency+t)* amplitude * 100;
    let offsetY = noise(i*frequency+t) * amplitude * 100;

    vertex(x+offsetX,y+offsetY);
  }
  for (i = 0; i < 1; i += 0.002) {
    let x = lerp(endX2, startX2, i);
    let y = lerp(endY2, startY2, i);

    // hint: drive offsetX and offsetY with noise() instead of random()
    let offsetX = noise(i*frequency+t)* amplitude * 100;
    let offsetY = noise(i*frequency+t) * amplitude * 100;

    vertex(x+offsetX,y+offsetY);
  }
  endShape(CLOSE);
}

