function drawMotif(x,y, l, dir){
  myTurtle.penUp();
  myTurtle.moveTo(x,y);
  myTurtle.turnTo(0);
  myTurtle.penDown();

  period = 2000;
  a = map(millis(), 0, period, -60, 60);

  turnA = sin(radians(a)) * 60;


  stroke(0,0,0,0)
  strokeWeight(1);
  myTurtle.pushState();
  for (let i = 0; i < 6; i++) {
    myTurtle.moveForward(l);
    c = color(colArray[i]);
    c.setAlpha(5);
    stroke( map(x,0,width,50,247), map(y,0,height,60,120), 19,5);
    // stroke(c)
    myTurtle.turnLeft(dir * turnA);
    drawTriangles(l);
  }
  myTurtle.popState();
}
}

function drawTriangles(l) {

  myTurtle.pushState();
  for (let i = 0; i < 6; i++) {

    myTurtle.pushState();
    myTurtle.moveForward(-l);

    myTurtle.pushState();
    for (let j = 0; j < 3; j++) {
      strokeWeight(30);

      myTurtle.turnLeft(l*4);
      myTurtle.moveForward(-l*2);
    }
    myTurtle.popState();
    myTurtle.popState();
  }
  //   for (let j= 0; j <10;  j++){
  //   myTurtle.turnLeft(turnA);
  //   myTurtle.moveForward(-5);
  // }
  myTurtle.popState();
}

