// require https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js

let frequency_slider;
let amplitude_slider;
let time_slider;
let t = 0;

let startX = 50;
let startY = 250;
let endX = 450;
let endY = 50;



function setup() {
  createCanvas(500, 300);

  createP("Frequency");
  frequency_slider = createSlider(0, 500, 50);

  createP("Amplitude");
  amplitude_slider = createSlider(0, 100, 50);

  createP("Time Speed");
  time_slider = createSlider(0, 100, 50);
}

function draw() {
  background(50);
  ellipseMode(CENTER);

  let frequency = frequency_slider.value() / 10;
  let amplitude = amplitude_slider.value() / 100;
  let timespeed = time_slider.value() / 1000;

  //similar to frame rate but needs its own variables
  t += timespeed;
  noiseDetail(1, 0.5);



  fill(255);
  noStroke(); 

  // study this loop. do you understand how the line of ellipses is created?

  // lerp() calculates a number between two numbers at a specific increment.
  // The amt parameter is the amount to interpolate between the two values
  // where 0.0 equal to the first point, 0.1 is very near the first point, 0.5
  // is half-way in between, etc.

  for (i = 0; i < 1; i += 0.02) {
    let x = lerp(startX, endX, i);
    let y = lerp(startY, endY, i);

    let offsetX = noise(i * frequency + t) * amplitude * 100;

    //Y needs higher offset so changed the seed here
    let offsetY = noise(i * frequency + t, 50) * amplitude * 100;

    ellipse(x + offsetX, y + offsetY, 10, 10);
  }
}

