class Particle {
  constructor(x,y,iteration) {
    this.x = x;
    this.y = y;
    this.seed = iteration;
    this.a = random(0, 2 * PI);
    this.a2 = random(0, 2 * PI);
    this.turn_speed = random();
    this.speed = random();
    this.diameter = map(noise(this.seed*noiseFrequency,height),0,1,5,20);
  }


  draw() {
    push();
    translate(this.x, this.y);
    rotate(this.a);
    fill(map(this.diameter,5,20,0,400),100,100);
    stroke(1);
    stroke("white");
    ellipse(0,0,this.diameter);
    pop();
  }
  update(){
    this.x-= sin(this.a) * map(this.speed, 0, 1, min_speed, max_speed);
    this.y+= sin(this.a2) * map(this.speed, 0, 1, min_speed, max_speed);
    if(this.x>width ||this.x<0){
    this.a=-this.a;
    }
    if(this.y>height ||this.y<0){
      this.a2=-this.a2
    }
  }
}

