function DrawMotif(x,y,div,col){
    var mainRect1 = new Rectangle([x+offset,y+offset],[scale-offset,scale-offset])
    var mainPath1 = new Path.Rectangle(mainRect1);
    mainPath1.strokeColor = "green"

    var mainRect2 = new Rectangle([x+offset + (scale-offset)/div,y+offset +(scale-offset)/div],[(scale-offset)/2,(scale-offset)/2]);
    var mainPath2 = new Path.Rectangle(mainRect2);
    mainPath1.strokeColor = "green"

    var centerRect = new Rectangle([x+offset + 3*(scale-offset)/8,y+offset + 3*(scale-offset)/8],[(scale-offset)/4,(scale-offset)/4]);
    var centerPath = new Path.Rectangle(centerRect);
    centerPath.rotate(45)
    centerPath.fillColor = "red"

    var basePath = mainPath1.subtract(mainPath2);
    basePath.fillColor = "yellow";
    mainPath1.remove();
    mainPath2.remove();



    var sides= []
    sides.push(basePath);
    sides.push(centerPath)

            var miniRect1 = new Rectangle([x,y],[100,100])
            var miniPath1 = new Path.Rectangle(miniRect1);
            miniPath1.rotate(45)
            miniPath1.strokeColor = "black";
            sides.push(miniPath1)

            var miniRect2 = new Rectangle([x+200,y],[100,100])
            var miniPath2 = new Path.Rectangle(miniRect2);
            miniPath2.rotate(45)
            miniPath2.strokeColor = "black";
            sides.push(miniPath2)

            var miniRect3 = new Rectangle([x+200,y+200],[100,100])
            var miniPath3 = new Path.Rectangle(miniRect3);
            miniPath3.rotate(45)
            miniPath3.strokeColor = "black";
            sides.push(miniPath3)

            var miniRect4 = new Rectangle([x,y+200],[100,100])
            var miniPath4 = new Path.Rectangle(miniRect4);
            miniPath4.rotate(45)
            miniPath4.strokeColor = "black";
            sides.push(miniPath4)

            var unitedBase = unite(sides);
            unitedBase.fillColor = "green"

            var miniSides = []
            for(var i = 0; i<=200; i+=200){
                for (var j = 0; j<=200; j+=200){
                    var sideRect = new Rectangle([x+i+offset/2,y+j+offset/2],[50,50])
                    var sidePath = new Path.Rectangle(sideRect);
                    sidePath.fillColor = "yellow"
                    miniSides.push(sidePath);

                }
            }
            var allSides = unite(miniSides);
            allSides.fillColor = "purple";

            var cutout = unitedBase.subtract(allSides)
            // cutout.fillColor = "black"
            cutout.style = {
                // strokeColor: "#eda51f",
                strokeColor: "black",
  fillColor: new Color(1,0.01*col,0.05*col),
// fillColor: col,
  strokeWidth: 4,
  strokeCap: "round",
  dashArray: [4, 10],
  shadowColor: new Color(0, 0, 0, 0.7),
  shadowBlur: 12,
  shadowOffset: new Point(5, 5),
            }

            allSides.remove();
            unitedBase.remove();


}

function unite(a) {
  var path = a[0];
  path.remove();

  for (var i = 1; i < a.length; i++) {
    a[i].remove();
    path = path.unite(a[i]);
  }
  project.activeLayer.addChild(path);
  return path;
}

