            translate([0, 0, 1.5]) cube([10, 10, 6], center = true);
            translate([0,0,-4]) cube([10, .25, 2.01], center = true);
            translate([0,0,-4]) cube([.25, 10, 2.01], center = true);
        }
    }
}

module dome() {
    translate([0, 0, -.2]) // merge base
