      img.set(row * random(50), col * random(75), colorPix);
    }
  }

  noSmooth();

  img.updatePixels();
  image(img, 0, 0, width, height);

  push();
  filter(BLUR, 3);
  image(img, 2, 3.5, width, height);
  pop();

  noLoop();
}

