function drawGrassBody(x, y, angle, dir) {
  myTurtle.pushState();
  myTurtle.penUp();
  myTurtle.moveTo(x, y);
  myTurtle.penDown();
  myTurtle.turnLeft(100);

  drawGrassPart(angle, dir);

  myTurtle.popState();
}

function drawGrassPart(angle, dir) {
  for (let i = 0; i < 15; i++) {
    strokeWeight(map(i, 0, 18, 3, 1));

    if (i > 2 && i < 7) {
      angle += 2;
    }
    myTurtle.moveForward(10);
    myTurtle.turnRight(dir * angle);
  }

  push();
  fill(random(100, 135), 111, random(180, 209));
  drawFlower(myTurtle.x, myTurtle.y);
  pop();
}

function drawFlower(x, y) {
  push();
  noStroke();
  ellipse(x, y, random(6, 10));
  pop();
}

function drawLeaf() {
  push();
  fill(random(20, 100), 111, random(10, 99));
  noStroke();
  ellipse(myTurtle.x, myTurtle.y, random(5, 15), random(10, 20));
  pop();
}

function drawArchFlower(x, y) {
  myTurtle.pushState();
  myTurtle.penUp();
  myTurtle.moveTo(x, y);
  myTurtle.turnTo(90);
  myTurtle.penDown();

  for (let u = 0; u < 15; u++) {
    myTurtle.moveForward(3);
    myTurtle.turnRight(random(-1, 3));
    // myTurtle.penUp();
    myTurtle.turnLeft(random(-1, 1));
    myTurtle.moveForward(3);
  }

  myTurtle.popState();
}

function drawArch(x, y, thickness, length) {
  myTurtle.pushState();
  myTurtle.penUp();
  myTurtle.moveTo(x, y);
  myTurtle.penDown();
  push();
  strokeWeight(thickness);

  myTurtle.turnLeft(90);
  myTurtle.moveForward(length);

  for (let i = 0; i < 36; i++) {
    myTurtle.moveForward(10);
    myTurtle.turnRight(5);

    // if(i %6==0)
    // {
    //   push();
    //   stroke(0,255,0)
    //   drawDiagonals(myTurtle.x, 200, 25,8,45);
    //   pop();
    // }
  }

  myTurtle.moveForward(length + 10);
  pop();

  myTurtle.popState();

  for (let j = 0; j < 7; j++) {
    push();
    stroke(150, 200, 100);

    drawDiagonals(170, 580 - j * 50, 25, 8, 45);

    drawDiagonals(215, 570 - j * 50, 18, 8, 45);

    drawDiagonals(400, 580 - j * 50, 25, 8, 45);
    drawDiagonals(445, 570 - j * 50, 18, 8, 45);

    push();
    stroke(random(220, 255), 51, random(220, 255));
    strokeWeight(random(1.5, 3));
    drawArchFlower(random(170, 190), 575 - j * 50);
    drawArchFlower(random(215, 225), 575 - j * 50);
    drawArchFlower(random(400, 420), 575 - j * 50);
    drawArchFlower(random(445, 460), 575 - j * 50);
    pop();

    pop();
  }
}

function drawDiagonals(x, y, length, thickness, angle) {
  myTurtle.pushState();
  myTurtle.penUp();
  myTurtle.moveTo(x, y);
  myTurtle.penDown();
  strokeWeight(thickness);

  myTurtle.turnLeft(angle);
  myTurtle.moveForward(length);

  myTurtle.popState();
}

function drawBranch(length) {
  if (length < 5) {
    drawLeaf();
    return;
  }

  // draw this branch
  strokeWeight(length / 6);

  myTurtle.moveForward(length * 0.75);
  myTurtle.turnLeft(random(-2));
  myTurtle.moveForward(length * 0.5);

  // left child
  myTurtle.pushState();
  myTurtle.turnLeft(random(5, 25));
  drawBranch(length * 0.75);

  myTurtle.popState();

  // right child
  myTurtle.pushState();
  myTurtle.turnRight(random(30, 55) * 0.55);
  drawBranch(length * 0.75);
  myTurtle.popState();
}

