function nonCircle(locationX, locationY, radius, warp){

    // Draws a circle, changes its hue slightly, and moves its segments points by a random amount
    var doodlyCircle = new Path.Circle([locationX, locationY], radius);
    doodlyCircle.fillColor = "red";
    doodlyCircle.fillColor.hue += randColor - Math.random() * 100

    for (var i = 0; i < doodlyCircle.segments.length; i++){

        randOffset = Math.random() * warp - (warp);

        doodlyCircle.segments[i].point += randOffset
    }

    return doodlyCircle;

}

function drawDoodly(locationX, locationY, radius, warp, amount){

    for (var i = 0; i < amount; i++) {
        nonCircle(locationX, locationY,radius, warp);
    }

}

function createGrid(number) {

    for (var i = 0; i < number; i++){
        for (var j = 0; j < number; j++){

            var randSize = Math.random(0) + Math.random(1) + Math.random(2) + Math.random(3) + Math.random(4)
            randSize = randSize/5

            drawDoodly(500/number * i + 50, 500/number * j + 50, randSize*10, 30, 1)
        }
    }

}

