//referring to the Coding Train tutorial on Reaction Diffusion

var grid;
var next;

var dA = 1;
var dB = 0.5;
var feed = 0.055;
var k = 0.062;

function setup() {
  createCanvas(400, 400);
  pixelDensity(1);
  grid = [];
  next = [];
  for (i = 0; i < width; i++) {
    grid[i] = [];
    next[i] = [];
    for (j = 0; j < height; j++) {
      grid[i][j] = { a: 1, b: 0 };
      next[i][j] = { a: 1, b: 0 };
    }
  }

  for (x = width*0.25-10; x < width*0.25+10; x++){
    for (y = width*0.25-10; y < width*0.25+10; y++){
      grid[x][y].b = 1
    }
  }

  for (x = width*0.75-10; x < width*0.75+10; x++){
    for (y = width*0.75-10; y < width*0.75+10; y++){
      grid[x][y].b = 1
    }
  }

  for (x = width*0.75-10; x < width*0.75+10; x++){
    for (y = width*0.25-10; y < width*0.25+10; y++){
      grid[x][y].b = 1
    }
  }

  for (x = width*0.25-10; x < width*0.725+10; x++){
    for (y = width*0.75-10; y < width*0.75+10; y++){
      grid[x][y].b = 1
    }
  }


}

function draw() {
  background(39, 92, 41);

  for (i = 1; i < width-1; i++) {
    for (j = 1; j < height-1; j++) {
      var a = grid[i][j].a;
      var b = grid[i][j].b;
      next[i][j].a = a + (dA * laplaceA(i,j)) - (a * b * b) + (feed * (1 - a));
      next[i][j].b = b + (dB * laplaceB(i,j)) + (a * b * b) - ((k + feed) * b);

      next[i][j].a = constrain(next[i][j].a, 0,1)
      next[i][j].b = constrain(next[i][j].b, 0,1)
    }
  }


  loadPixels();
  for (i = 0; i < width; i++) {
    for (j = 0; j < height; j++) {
      var pix = (i + j * width) * 4;
      var a = next[i][j].a
      var b = next[i][j].b
      var c = floor((a-b)*150);
      c = constrain(c, 0, 255);

      pixels[pix + 0] = c+50;
      pixels[pix + 1] = c-50;
      pixels[pix + 2] = c+50;
      pixels[pix + 3] =255;
    }
  }

  updatePixels();

  swap();
}

function swap() {
  var temp = grid;
  grid = next;
  next = temp;
}

function laplaceA(i,j) {
  var sumA = 0;

  sumA += grid[i][j].a * -1;
  sumA += grid[i - 1][j].a * 0.2;
  sumA += grid[i + 1][j].a * 0.2;
  sumA += grid[i][j + 1].a * 0.2;
  sumA += grid[i][j - 1].a * 0.2;
  sumA += grid[i + 1][j + 1].a * 0.05;
  sumA += grid[i - 1][j - 1].a * 0.05;
  sumA += grid[i + 1][j - 1].a * 0.05;
  sumA += grid[i - 1][j + 1].a * 0.05;

  return sumA;
}

function laplaceB(i,j) {
   var sumB = 0;

  sumB += grid[i][j].b * -1;
  sumB += grid[i - 1][j].b * 0.2;
  sumB += grid[i + 1][j].b * 0.2;
  sumB += grid[i][j + 1].b * 0.2;
  sumB += grid[i][j - 1].b * 0.2;
  sumB += grid[i + 1][j + 1].b * 0.05;
  sumB += grid[i - 1][j - 1].b * 0.05;
  sumB += grid[i + 1][j - 1].b * 0.05;
  sumB += grid[i - 1][j + 1].b * 0.05;

  return sumB;
}

