function DrawRope(dir, h, col) {
  for (i = 0; i < 1; i += 0.03) {
    let x = lerp(startX, endX, i);
    let y = lerp(startY, endY, i);

    // tried multiplying by the frameSpeed but that was too too fast
    n = noise(i * frequency * 30 + frameSpeed);
    offsetX = (n - 0.5) * amplitude *dir * 100;
    offsetY = (n - 0.5) * amplitude *dir * -500;

    push()
    fill(col);
    noStroke();
    translate(0,h)
    rect(x + offsetX, y + offsetY, 10, 5, 5);
    // image(rope, x + offsetX, y + offsetY, 15, 15)

    pop()
  }

  push();
  translate(endX, endY + offsetY);
  translate(0,h)
  rotate(n);
  // rect(0, 0, 50, 10);
  image(hand, 0,0, hand.width/10, hand.height/10)
  pop();
}

