	myColor = color(hue(temp) + randomGaussian() * scale,
		saturation(temp) + randomGaussian()*scale,
		brightness(temp) + randomGaussian()*scale,
		random(10,100));
	return myColor;
}


function setGradientStroke(pos,rad)
{
	gradient = drawingContext.createRadialGradient(pos.x,pos.y,0,pos.x,pos.y,rad);
	gradient.addColorStop(0,generateColor(1));
	gradient.addColorStop(0.5,generateColor(1));
	gradient.addColorStop(1,generateColor(1));
	drawingContext.strokeStyle = gradient;
}

function setGradientFill(pos,rad)
{
	gradient = drawingContext.createRadialGradient(pos.x,pos.y,0,pos.x,pos.y,rad);
	gradient.addColorStop(0,generateColor(1));
	gradient.addColorStop(0.5,generateColor(1));
	gradient.addColorStop(1,generateColor(1));
	drawingContext.fillStyle = gradient;
}

