	resetCanvas();
}

/* ----------------------------- Main ----------------------------- */

function setup() {
	createCanvas(windowWidth, windowHeight);
	colorMode(HSB, 100);
	noStroke();

	background(10);
	updateParticles();
	setInterval(updateParticles, 2500);
}

function draw() {
	for (let p of particles) {
		p.draw();
		p.move();
	}
}

function updateParticles() {
	particles = [];

	for (let i = 0; i < params.numParticle; i++) {
		let x_ = random(width);
		let y_ = height;
		let s_ = params.circleSize;
		let c_ = color((random(15) + acc) % 100, 80, 93, 100);
		particles.push(new Particle(x_, y_, s_, c_));
	}
	acc += params.colorZone;
}

function Particle(x_, y_, s_, c_) {
	this.x = x_;
	this.y = y_;
	this.size = s_;
	this.c = c_;

	this.alpha = 100;
	this.dist = 1;

	this.move = function () {
		let theta =
			noise(this.x * params.squiggliness, this.y * params.squiggliness) * PI * params.angle;
		let v = p5.Vector.fromAngle(theta, this.dist);
		this.x += v.x;
		this.y += v.y;
		this.dist *= 0.9999;
		this.alpha *= params.lineLength;
	};

	this.draw = function () {
		this.c.setAlpha(this.alpha);
		fill(this.c);
		circle(this.x, this.y, this.size);
		this.y -= random(-2,8);
		this.c.setAlpha(100);
	};
}

