const PARAMS = {
	frequency: 0.2,
	amplitude: 50,
	speed: 0.02,
};
const pane = new Tweakpane.Pane();
pane.addInput(PARAMS, "frequency", { min: 0, max: 0.5, step: 0.005 });
pane.addInput(PARAMS, "amplitude", { min: 0, max: 100, step: 10 });
pane.addInput(PARAMS, "speed", { min: 0, max: 0.05, step: 0.005 });

function setup() {
	createCanvas(500, 400);
	textFont(font);
	angleMode(DEGREES);
	ellipseMode(CENTER);
	textAlign(CENTER, CENTER);
	colorMode(HSB);
	//frameRate(1);

	//textptsArray = createPointText("H", 0,0, 32, 1, 0);
	arr01 = createPointText("M", 0, 0, 32, 3, 0);
	arr02 = createPointText("V", 0, 0, 32, 3, 0);
	arr03 = createPointText("P", 0, 0, 32, 3, 0);
}

function draw() {
	background(20);
	noiseDetail(1, 0.5);
	fill(255);

	let arrNum = 3;
	drawTextOutline(arr01, 1/arrNum, 0, height / 3);
	drawTextOutline(arr02, 1/arrNum, width / arrNum, height / 3);
	drawTextOutline(arr03, 1/arrNum, (width / arrNum) * 2, height / 3);
}

function drawTextOutline(txtarr, scal, _x, _y) {
	push();
	translate(_x, _y);
	scale(scal);
	for (i = 0; i < txtarr.length; i++) {
		let x = txtarr[i].x;
		let y = txtarr[i].y;
		const n1 = noise(
			i * PARAMS.frequency + x * 0.01 + frame,
			i * PARAMS.frequency  + frame
		);

		let offsetX = n1 * PARAMS.amplitude;
		let offsetY = n1 * PARAMS.amplitude;
		stroke(220);
		line(
			x + offsetX,
			y + offsetY,
			x + offsetX - 100 * n1,
			y + offsetY - 5000 * n1 * osc(0.5)
		);
		pop();
	}
	frame += PARAMS.speed;
}

function createPointText(text, x, y, fontSize, sampleFactor, simplifyThreshold) {
	let textPoints = font.textToPoints(text, x, y, fontSize, {
		sampleFactor: sampleFactor,
		simplifyThreshold: simplifyThreshold,
	});
	let bounds = font.textBounds(text, x, y, fontSize);

	textPoints.forEach((letter) => {
		letter.x = (letter.x * (width - 100)) / bounds.w + 40;
		letter.y = (letter.y * (height - 100)) / bounds.h + (height - 50);
	});
	return textPoints;
}

function osc(scale) {
	if (!scale) {
		scale = 1;
	}
	return sin(frameCount * 0.01 * scale);
}

function keyPressed() {
	if (keyCode == 83) {
		saveCanvas("img", "jpg");
	}
}

