// require https://cdnjs.cloudflare.com/ajax/libs/paper.js/0.12.15/paper-full.min.js

var path, position, max;
var count = 0;
var grow = false;
var i;

var raster = new Raster('painting');
var radius = 1;
var space = 1;
raster.visible = true;

function onFrame(event) {
	if (grow) {
	    raster.size = new Size(50);
	    drawRect();
	}
}

function drawRect(){
for (var y = 0; y < raster.height; y++) {
		for(var x = 0; x < raster.width; x++) {
			var color = raster.getPixel(x, y);
			var path = new Path.Circle({
				center: new Point(x, y) * radius,
				radius: gridSize / 2 / space,
				fillColor: 'gray'
			});

			path.scale(1 - color.gray);
		}
	}
grow=false;
}

function resetSpiral() {
	grow = true;

	// Transform the raster, so it fills the view:
	raster.fitBounds(view.bounds);

	if (path)
		path.remove();

	position = view.center;
	count = 0;
	path = new Path({
		fillColor: 'black',
		closed: true
	});

	position = view.center;
	max = Math.min(raster.bounds.width, raster.bounds.height) * 0.5;
}

function onDocumentDrag(event) {
	event.preventDefault();
}

function onDocumentDrop(event) {
	event.preventDefault();

	var file = event.dataTransfer.files[0];
	var reader = new FileReader();

	reader.onload = function (event) {
		var image = document.createElement('img');
		image.onload = function () {
			raster = new Raster(image);
			raster.visible = false;
			resetSpiral();
		};
		image.src = event.target.result;
	};
	reader.readAsDataURL(file);
}

document.addEventListener('drop', onDocumentDrop, false);
document.addEventListener('dragover', onDocumentDrag, false);
document.addEventListener('dragleave', onDocumentDrag, false);

