// Dot Challenge Starting Point

function setup() {
  createCanvas(500, 500);
}

function draw() {
  background(50);

  noStroke();
  // ellipseMode(CENTER);

  let noiseFrequency = 0.2;

  for (let row = 0; row < 10; row++) {
    for (let col = 0; col < 10; col++) {
      // these points are not scattered in the same way
      // how can you make the arrangement match the challenge?
      let x = random(width) + noise(row * noiseFrequency);
      let y = random(height) + noise(col * noiseFrequency);

      // the diameter shouldn't always be 10, it needs to vary
      let diameter = random(7, 22);

      // let r = map(diameter, 7, 20, 1, noise(1));
      // let g = map(diameter, 7, 20, 1, noise(1));
      // let b = map(diameter, 7, 20, 1, noise(1));

      //colors
      if (diameter <= 22 && diameter > 19) {
        fill(random(100, 130), random(10), random(90,120));
      } else if (diameter <= 19 && diameter > 17) {
        fill(0, random(35, 50), random(225, 255));
      } else if (diameter <= 17 && diameter > 9) {
        fill(random(70, 90), random(225, 255), random(100, 130));
      } else if (diameter <= 9 && diameter > 7) {
        fill(random(180, 220), random(90, 150), random(10));
      } else if (diameter <= 7 && diameter > 7) {
        fill(255, 0, 0);
      }

      ellipse(x * noise(row, 0) + random(50, 100), y * noise(col) + 100, diameter, diameter);
    }
  }

  noLoop();
}

