  drawBranch(50);
  myTurtle.penUp();
  myTurtle.moveTo(480, 580);
  myTurtle.penDown();
  drawPlant(15);
}

function drawBranch(length) {
  if (length < 4) {
    return;
  }

  // draw this branch
  strokeWeight(length / 7);
  stroke(98, 102, 89);
  myTurtle.moveForward(length * 0.7);
  myTurtle.turnLeft(5);
  myTurtle.moveForward(length * 0.6);

  // left child
  myTurtle.pushState();
  myTurtle.turnLeft(random(10, 40));
  drawBranch(length * 0.75);
  drawFlower();
  //drawBranch(length * random(.5, .9));
  myTurtle.popState();

  // right child
  myTurtle.pushState();
  myTurtle.turnRight(random(20, 40));
  drawBranch(length * 0.75);
  drawFlower();
  //drawBranch(length * random(.5, .9));
  myTurtle.popState();
}

function drawPlant(length) {
  strokeWeight(length / 5);
  stroke(20, 30, 20);
  myTurtle.turnRight(10);

  myTurtle.pushState();
  myTurtle.turnLeft(random(10, 30));
  drawBranch(length);
  myTurtle.popState();
}

function drawFlower() {
  for (let j = 0; j < 25; j++) {
    myTurtle.turnRight(20);
    for (let i = 0; i < 5; i++) {
      strokeWeight(0.01);
      stroke("#9AEBA3");
      myTurtle.moveForward(15);
      myTurtle.turnRight(300);
      myTurtle.turnLeft(10);
    }
  }
}
function drawGrass() {
  myTurtle.pushState();

  for (let xPos = 0; xPos < 800; xPos += 2) {
    myTurtle.penUp();
    let yPos = random(0.3, 0.9);
    myTurtle.moveTo(xPos, 800 * yPos);
    myTurtle.turnTo(-90);
    myTurtle.penDown();
    // draw one grass
    let slopeR = 0.1;
    let grassSlopeR = 1 * random(0, 2);
    let slopeL = 0.2 * random(0.9, 1.1); //
    let grassSlopeL = grassSlopeR;
    let randomNum = random();
    let grassLengthR = 90 * yPos;
    let grassLengthL = 120 * yPos;
    if (random() < 4 / 7) {
      stroke("#076630");
    } else {
      stroke("#0CA64E");
    }
    for (let j = 0; j < grassLengthR; j++) {
      myTurtle.moveForward(grassSlopeR);
      if (randomNum <= 1 / 3) {
        myTurtle.turnRight(grassSlopeR * slopeR);
      } else {
        myTurtle.turnLeft(grassSlopeL * slopeL);
      }
    }
    for (let j = 0; j < grassLengthL; j++) {
      if (randomNum <= 1 / 3) {
        myTurtle.turnLeft(grassSlopeL * slopeL);
      } else {
        myTurtle.turnRight(grassSlopeR * slopeR);
      }
      myTurtle.moveBackward(grassSlopeL);
    }
  }

  myTurtle.popState();
}

function drawGrass_noise() {
  push();
  let n;

  stroke(0, 0, 0, 50);

  for (let x = 15; x < width - 15; x += 1.5) {
    let offsetX = 0;
    let offsetY = 0;

    // lean (low frequency, static)
    n = noise(x * 0.1);
    offsetX += n * 20 - 10;

    // height (high frequency sampling yields independent values)
    offsetY += noise(x * 10) * 20;

    // wind( lower frequency, timed)
    n = noise(x * 0.01 + millis() * -0.001);
    offsetX += n * 40;

    line(x, 790, x + offsetX, 600 + offsetY);
  }
  pop();
}

