// let alp_offset = 1;
noiseMax = 0.5;

function setup() {
  createCanvas(600, 600);
  colorMode(HSB);
  blendMode(SCREEN);
  // background();
}

function draw() {
  bubble(width / 2 + 50, height / 2 , 0);
  bubble(200, 300, 100);
  // bubble(400, 200, 180);
}

function bubble(centerX, centerY, t) {
  push();
  translate(centerX, centerY);
  noStroke();
  fill((frameCount + t) % 255, 255, 255, 0.05);
  beginShape();
  for (let a = 0; a < TWO_PI; a += 0.01) {
    let xoff = map(cos(a + rotate), -1, 1, 0, noiseMax);
    let yoff = map(sin(a + rotate), -1, 1, 0, noiseMax);
    let ra = map(
      noise(xoff, yoff, frameCount / 500 + t / 10),
      0,
      1,
      0,
      height / 4
    )*2 + frameCount/200;
    let x = ra * cos(-a);
    let y = ra * sin(-a);
    // fill(0,10);
    // ellipse(x, y, 1);
    vertex(x, y);

    // col_offset += 1;
    rotate += (cos(a) + sin(a)) / 50;
    // alp_offset += sin(a) / 100;
    // console.log(alp_offset);
  }
  endShape(CLOSE);
  pop();
}

