function draw() {
  space = spaceSlider.value();
  intSpace = intSpaceSlider.value();
  size = sizeSlider.value();
    if(intSpaceSlider.value()<80){
     fill(random(col));
  }

  if (space != prevSpace || intSpace != prevIntSpace || size != prevSize) {
    drawShapes();
  }
}

function drawShapes() {
  background(0);
  for (x = -50; x < width + 50; x += space) {
    for (y = -50; y < height + 50; y += space) {
      for (i = 0; i < 8; i++) {
        push();
        translate(x, y);
        rotate(i * 45);
        beginShape();
        curveVertex(intSpace + size, 0);
        curveVertex(intSpace + size, 0);
        curveVertex(intSpace + size / 4, -size / 4);
        curveVertex(intSpace, 0);
        curveVertex(intSpace + size / 4, +size / 4);
        curveVertex(intSpace + size, 0);
        curveVertex(intSpace + size, 0);
        endShape();
        pop();
      }
    }
  }
  for (x = -50 + space / 2; x < width + 50; x += space) {
    for (y = -50 + space / 2; y < height + 50; y += space) {
      for (i = 0; i < 8; i++) {
        push();
        translate(x, y);
        rotate(i * 45);
        beginShape();
        curveVertex(intSpace + size, 0);
        curveVertex(intSpace + size, 0);
        curveVertex(intSpace + size / 4, -size / 4);
        curveVertex(intSpace, 0);
        curveVertex(intSpace + size / 4, +size / 4);
        curveVertex(intSpace + size, 0);
        curveVertex(intSpace + size, 0);
        endShape();
        pop();
      }
    }
  }
  prevSpace = space;
  prevIntSpace = intSpace;
  prevSize = size;
}

