  story: ["You are #mood# becuase you are #hobby# #emoji#! #world# is waiting for tou to #averb#:) The #nature# is #emoji2#! You should keep #activities#  #emoji3#"],
  mood: ["doing awesome", "great","beatiful","cool","shnning"],
  hobby: ["being nice to others", "still good at playing games", "looks like someone born in 1975", "still walk faster than a 10 years old", "an idol to your grand kids", "unique","good at making good jokes"],
  world: ["Your friend ", "Your grand kids", "Your daughter", "Your son"],
  averb:["grab a cup of coffee","cook together","play games","read a book","go for a walk"],
  emoji:["🧠","🪴 ","🥸 ","👒 " ],
  nature: ["weather", "view", "market", "food"],
  emoji2:["awsome 💫","nice🌷 ","amazing🍃 ","gorgeous🐿 "],
  emoji3:["☺️","🤣 ", "😎 ",  "💖  "],
  activities:["doing great","dong the best","enjoy your life","enjoy the moment"],
};

function main() {
  const grammar = tracery.createGrammar(storyGrammar);
  const story = grammar.flatten("#story#");
  display(story);
}

function display(...strings) {
  const div = document.createElement("div");
  div.style = "font-size: 30px; margin: 10%; line-height: 1.5;";
  div.innerText = strings.join("\n");
  document.body.append(div);
}

// tracery doesn't load immediately, not sure why, so kludge it
setTimeout(main, 10);

