// require https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js
// require /turtles/turtle/turtle.js


let t, lineColor;
let xPos_horizon, yPos_horizon;
let rad; //random number storage value
const COLORS = {
	brown_root: '#3B1B08',
	branches: '#314F1B',
	sun: '#FFD292',
};

/* ------------------- start ------------------- */

function setup() {
	createCanvas(600, 600);
	lineColor = color('#E9F9FA');
	t = new Turtle();
	xPos_horizon = width / 2;
	yPos_horizon = height - 200;
}

function draw() {
	background(220);
	lineColor.setAlpha = random(90, 255);
	stroke(lineColor);

	t.penUp();
	t.moveTo(xPos_horizon, yPos_horizon);
	t.turnTo(-90);
	t.penDown();

	stroke(COLORS.branches);
	drawBranch(50);
	drawHorizon();
	stroke(COLORS.brown_root);
	drawRoots(120);

	noLoop();
}

function drawHorizon() {
	t.pushState();
	stroke(255);
	strokeWeight(1.2);
	// right side horizon
	t.penUp();
	t.moveTo(xPos_horizon, yPos_horizon);
	t.turnTo(0);
	t.penDown();

	for (let i = 0; i < 100; i++) {
		rad = random();
		t.moveForward(3.2);
		if (rad < 1 / 2) {
			t.turnLeft(0.48);
		} else {
			t.turnRight(0.42);
		}
	}
	// left side horizon
	t.penUp();
	t.moveTo(xPos_horizon, yPos_horizon);
	t.turnTo(180);
	t.penDown();
	for (let i = 0; i < 200; i++) {
		rad = random();
		t.moveForward(2.3);
		if (rad < 1 / 2) {
			t.turnLeft(0.6);
		} else {
			t.turnRight(0.62);
		}
	}
	t.popState();
}

function drawBranch(length) {
	if (length < 10) {
		return;
	}

	t.pushState();
	// draw this branch
	strokeWeight(length / 20);
	t.moveForward(length * 0.5);
	t.turnLeft(10);
	t.moveForward(length * 0.5);

	// left child
	t.pushState();
	t.turnLeft(random(20, 40));
	drawBranch(length * 0.5);
	t.popState();

	// left child
	t.pushState();
	t.turnLeft(random(0, 20));
	drawBranch(length * 0.5);
	t.popState();

	// right child
	t.pushState();
	t.turnRight(random(10, 50));
	drawBranch(length * random(0.5, 0.7));
	t.popState();

	t.popState();
}

function drawRoots(length) {

	t.pushState();

	if (length < 2) {
		return;
	}


	t.penUp();
	t.moveTo(xPos_horizon, yPos_horizon);
	t.turnTo(90);
	t.penDown();

	// draw this branch
	t.turnLeft(10);

	// left child
	t.pushState();
	t.turnLeft(random(0, 10));
	drawBranch(length *random(0.8, 0.9));
	t.popState();

	// left child
	t.pushState();
	t.turnLeft(random(30, 40));
	drawBranch(length * 0.9);
	t.popState();

	// left child
	t.pushState();
	t.turnLeft(length * 0.2);
	drawBranch(length * 0.8);
	t.popState();

	// right child
	t.pushState();
	t.turnRight(random(10, 20));
	drawBranch(length * random(0.8, 0.9));
	t.popState();

	// right child
	t.pushState();
	t.turnRight(random(40,50));
	drawBranch(length * random(0.8, 0.9));
	t.popState();

	// right child
	t.pushState();
	t.turnRight(length * 0.7);
	drawBranch(length * random(0.8, 0.9));
	t.popState();

	// right child
	t.pushState();
	t.turnRight(length * 0.2);
	drawBranch(length * 0.9);
	t.popState();

	t.popState();
}
