let music;
let fft;

function preload() {
  music = loadSound("hp.wav");
}

function setup() {
  createCanvas(400, 200);

  fft = new p5.FFT(0.9, 128);
  fft.setInput(music);

  startButton = createButton("start");
  startButton.mousePressed(start);

  stopButton = createButton("stop");
  stopButton.mousePressed(stop);
}

function start() {
  music.play(0, 2.5, 1);
  music.loop(0, 1.5, 1);
}

function stop() {
  music.pause();
}

function draw() {
  background(0,20);
  noStroke();

  const data = fft.analyze();

  for (let i = 0; i < 64; i++) {
    const x = map(i, 0, 64, 0, 400);
    const y = map(data[i], 0, 355, 190, 10);
    //blendMode(SCREEN)
    fill(data[i],data[i]+30,data[i]+100)
    ellipse(x, y-20, 3, 3);
    fill(data[i],data[i]+100,data[i]+30)
    ellipse(x, y-10, 5, 5);
    fill(data[i]+70,data[i]+100,data[i]+100)
    ellipse(x, y, 5, 5);
  }
}

