let sound;
let bgcolor;
let filter;
let t = 0;
let w = 500;

function preload() {
	sound = loadSound('./assets/数据变幻声.wav');
	filter = new p5.LowPass();
	sound.disconnect();
	sound.connect(filter);
}
function setup() {
	createCanvas(400, 400);
	colorMode(HSB);
	background(20);
	noStroke();
}

function draw() {
	background(20);
	if (sound.isPlaying()) {
		amp.setInput(sound);
		let rms = amp.getLevel();
		stroke(
			mouseX / 2,
			mouseY / 5,
			(width / 2 - dist(mouseX, mouseY, width / 2, height / 2)) / 3
		);
		//draw circle
		x = random(100, 255);
		y = random(100, 255);
		z = random(100, 255);
		fill(x, y, z, 50);
		let diameter = map(rms, 0, 1, 0, width / 2);
		circle(mouseX, mouseY, diameter);
		if (mouseIsPressed) {
			fill(x, y, z, 100);
			s = w / 2;
			translate(mouseX, mouseY);
			strokeWeight(1);
			for (i = 0; i < 2 * PI; i += (2 * PI) / 7) {
				for (j = 0; j < s; j += w / 56) {
					push();
					//stroke(w / 3, 2 * j, s, j * 4);
					rotate(i + (t * j) / w / 3);
					line(mouseX, mouseY, s - j, 0);
					pop();
				}
			}
			t += 0.1;
		}

		let freq = map(mouseX, 0, width, 0, 4000);
		let res = map(mouseY, 0, height, 40, 1);
		filter.set(freq, res);
	}
}

function mousePressed() {
	sound.loop();
	amp = new p5.Amplitude();
}

