// Dot Challenge Starting Point

function setup() {
  createCanvas(400, 400);
}

function draw() {
  background(50);
  colorMode(HSB);

  noStroke();
  ellipseMode(CENTER);

  //increase frequency to replicate the scatter effect
  let noiseFrequency = 3;

  for (let i = 0; i < 100; i++) {
    // these points are not scattered in the same way
    // how can you make the arrangement match the challenge?
    let x = noise(i)*width;
    let y = noise(i*noiseFrequency)*height;


    // diameter from 5 to 20
    let diameter = map(noise(i*noiseFrequency,height),0,1,5,20);

    // the colors also need to change
    // HSB, from 0 to over 360 so that it turns back to warm colors when at biggest
    fill(map(diameter,5,20,0,400),100,100);

    ellipse(x, y, diameter);
  }

  noLoop();
}

