let freq = 0.5;
let step = 5;
let col1 = "#F5F0EB";
let col2 = "#6E5816";

function setup() {
    createCanvas(600, 600);

    stroke(col2);
    strokeWeight(0.8);
    noFill();

    slider1 = createSlider(5, 100, 10, 5); //radius
    slider2 = createSlider(1, 400, 200, 10); //amplitude
    slider3 = createSlider(0.1, 10, 5, 0.1); //octaves
}

function draw() {
    background(col1);
    r1 = slider1.value();
    amp = slider2.value();
    oct = slider3.value();

    makeSpiral(50, r1, amp);
    noiseDetail(oct, 0.5);
}

function makeSpiral(numWheels, r1, amp) {

    const centerX = width / 2;
    const centerY = height / 2;

    let k = 0;
    let rad = 1;
    let pt = 0;
    beginShape();
    for (let a = 0; a < numWheels * TWO_PI; a += PI / 90) {
        r2 = r1 + noise(a * freq) * (amp + step * k);
        if (pt / 180 == 1) {
            k += 5;
            //console.log(pt, k);
        }
        x = centerX + sin(a) * r2;
        y = centerY + cos(a) * r2;
        point(x, y);
        pt++;
    }
    endShape();
}

