function wave(a) {
  push();
  // translate(width / 2, height / 2);
  rotate(a);
  for (let i = 0; i < width; i += step) {
    for (let j = 0; j < height; j += step) {
      // let n = noise(i, j, z);
      let n = map(
        noise(i * noiseVal, j * noiseVal, frameCount / 100),
        0,
        1,
        0,
        7
      );

      let c = colors[floor(n)];
      fill(c);
      // set(i, j, 100);
      ellipse((i / n) * 2, (j / n) * 2, step / 3);
      z = +0.01;
    }
  }
  pop();
}

