const MARGIN = 50
const COL = {
    'bg01': "#FFFDF6",
    'bg02': "#229987",
    'flower': '#a61b29',
    'grey': "#F5F0EB",
    'brown': "#6E5816"
}
const CIRCLE_STEPS = 360
const NOISINESS = 35 // * MODIFY * line curviness
var trees = [];

function setup() {
    createCanvas(800, 800);
    background(COL.bg01)

    let cellSize = (min(width, height)) - (MARGIN * 2)
    let rings = 20 // * MODIFY * rings: round(random(0.1, 0.5) * cellSize)

    tree = {
        graphics: createGraphics(cellSize, cellSize),
        gridX: MARGIN,
        gridY: MARGIN,
        center: createVector(cellSize / 2 * random(0.9, 1), cellSize / 2 * random(0.9, 1)), // * MODIFY * center points
        radius: random(1, 3) * 3 * rings, // * MODIFY * tree rADIUS: random(0.2, 0.45) * cellSize
        rings: rings,
        barkThickness: round(random(4, 7) * rings), // * MODIFY * the thickness of bark: round(random(40, 70))
        ptsInterpolation: random(), // * MODIFY * x1-x2, y1-y2 Interpolation: random(), ..
        ptsAmpHorizontal: 0.5, // * MODIFY * points left-right waving: 0.5, ..
        ptsAmpVertical: 1, // * MODIFY * points up-down scatter, otherwise will become straight lines: 7, ..

        cracks: new Array(round(random(10, 20)))
            .fill()
            .map(() => {
                if (random() > 0.7) { 
                    let startAngle = random(TWO_PI)
                    let endAngle = startAngle + noise(startAngle) * 0.015
                    let startRing = random(0, rings * 0.5)
                    return [startAngle, endAngle, startRing, rings]
                } else { 
                    let startAngle = random(TWO_PI)
                    let endAngle = startAngle + TWO_PI * random(0.02, 0.1)
                    let startRing = random(rings * 0.3, rings * 0.85)
                    return [startAngle, endAngle, startRing, startRing + round(random(1, 2))]
                }
            })
    }

    noLoop()
}



function draw() {


    let g = tree.graphics;
    g.noFill()
    g.strokeWeight(0.2) // * MODIFY * points weight
    g.stroke(COL.flower)


    for (let ring = 0; ring < tree.rings; ring++) {

        let r = tree.radius * (ring + 1) / tree.rings // current radius for each ring
        let rOffset = map(
            noise(ring, r * 0.05),
            0, 1, -tree.ptsAmpVertical, tree.ptsAmpVertical);
        r += rOffset;
        let isLastRing = ring == tree.rings - 1

        let passes = isLastRing ? tree.barkThickness : 1 //Conditional (ternary) operator
        for (let j = 0; j < passes; j++) {
            for (let angle = 0; angle < TWO_PI; angle += TWO_PI / CIRCLE_STEPS) {

                // Leave certain areas blank to create cracks
                if (tree.cracks.find(r => angle > r[0] && angle < r[1] && ring > r[2] && ring < r[3])) {
                    continue
                }

                let rr = (r + j * 0.5) // ring radius, adjusted for pass
                let x1 = tree.center.x + cos(angle) * rr
                let y1 = tree.center.y + sin(angle) * rr
                let noiseOffset = map(
                    noise(
                        tree.gridX + x1 * 0.015,
                        tree.gridY + y1 * 0.015,
                        ring * 0.015
                    ),
                    0, 1, -NOISINESS, NOISINESS)
                x1 += noiseOffset
                y1 += noiseOffset

                let angle2 = angle + TWO_PI / CIRCLE_STEPS
                let x2 = tree.center.x + cos(angle2) * rr
                let y2 = tree.center.y + sin(angle2) * rr
                let noiseOffset2 = map(
                    noise(
                        tree.gridX + x2 * 0.015,
                        tree.gridY + y2 * 0.015,
                        ring * 0.05
                    ),
                    0, 1, -NOISINESS, NOISINESS)
                x2 += noiseOffset2
                y2 += noiseOffset2

                let sandGrains = isLastRing ? r * 2 : r * 0.2 
                for (let i = 0; i < sandGrains; i++) {
                    let x = lerp(x1, x2, tree.ptsInterpolation) + random(-1, 1) * tree.ptsAmpHorizontal;
                    let y = lerp(y1, y2, tree.ptsInterpolation) + random(-1, 1) * tree.ptsAmpHorizontal;
                    //g.stroke(COL.brown);
                    g.point(x, y);
                    //console.log(i, x, y);
                }
                // g.stroke('red');
                // g.point(x1, y1);

                //"Hairline Cracks"
                //from center to current ring 's edge
                if (j == 0 && noise(x1 * 0.1, y1 * 0.1) > 0.65) {
                    g.push()
                    g.strokeWeight(0.25)
                        //sandLine(g, tree.center.x, tree.center.y, x1, y1, rr * 0.2)
                    for (let i = 0; i < sandGrains; i++) {
                        let x = lerp(tree.center.x, x1, rr * 0.2) + random(-1, 1) * tree.ptsAmpHorizontal;
                        let y = lerp(tree.center.y, y2, rr * 0.2) + random(-1, 1) * tree.ptsAmpHorizontal;
                        g.point(x, y);
                    }
                    g.pop()
                }
            }
        }

    }
    let cellSize = (min(width, height)) - (MARGIN * 2)
    image(g, tree.gridX, tree.gridY);
}

