function drawLotus(x, y, length, col, rot) {
  timeInterval = 5000;
  angle = map(millis() % timeInterval, 0, timeInterval, 0, PI);
  alphaVal = map(sin(angle), -1, 1, 255, 100);

  for (let j = 0; j < 120; j++) {
    myTurtle.pushState();
    myTurtle.penUp();
    c = color(col);
    c.setAlpha(alphaVal);
    stroke(c);
    strokeWeight(10)
    myTurtle.moveTo(x, y);
    // myTurtle.turnLeft(0)

    if (j % 10 == 0 ){
      strokeWeight(0.1)
      stroke(255)
    }
    myTurtle.penDown();

    myTurtle.turnTo(j * 3 * sin(angle));

    myTurtle.turnLeft(rot);

    myTurtle.moveForward(length);

    myTurtle.popState();
  }

  drawBud(noise(width) * width, noise(height) *200)
}

function drawBud(x,y){
  fill(181, 96, 154, 30)
  noStroke()
  triangle(x,y, x-55, y+ 165, x+55, y+165)
  arc(x,y+165, 110, 120, 0, PI)

  push();
  noFill();
  stroke(110, 145, 90)
  line(x,y+200,x,height)
  // arc(x, y+ (height - (y+165))/2, 15, height - (y+165)/2, PI+HALF_PI, HALF_PI)
  pop();
}

function drawWave(xOffset){
  distance = 10;
  w  = width;
  theta += 0.02;
  amp = 10;
  period = 500
  // dy = 0.2;
  dy = (TWO_PI /period) * distance;
  xVal.push(floor(w/distance))

  push()
  y = theta;
  fill(255, 255, 255,15)
  noStroke();
  for(let i = 0; i < xVal.length; i++){
    xVal[i] = sin(y) * amp;
    y += dy
    // for( let j = 0; j <xVal.length; j++){
          ellipse(xOffset + xVal[i],y* distance,5)

    // }
  }
  pop();
  // console.log(theta)
  if (theta > 30){
    theta = 0;
  }
}

