let amplitude_slider, frequency_slider, timespeed_slider;

let startX = 50;
let startY = 250;
let endX = 450;
let endY = 50;
let r = 150;
let dotSize;

function setup() {
  createCanvas(500, 500);

  createP("Frequency");
  frequency_slider = createSlider(0, 100, 50);
  createP("Amplitude");
  amplitude_slider = createSlider(0, 100, 50);
  createP("Time Speed");
  timespeed_slider = createSlider(0, 100, 50);
  background(255);
}

function draw() {

  ellipseMode(CENTER);

  let amplitude = amplitude_slider.value() / 100;
  let frequency = frequency_slider.value() / 10;
  let timespeed = timespeed_slider.value() / 500;

  noiseDetail(1, 0.5);

  noStroke();

  // study this loop. do you understand how the line of ellipses is created?
  for (i = 0; i < 1; i += 0.002) {
    //     let x = lerp(startX, endX, i);
    //     let y = lerp(startY, endY, i);
    let x = (r + frameCount/30)* sin(i * 3 + (frameCount * timespeed) / 5) + width / 2;
    let y = (r + frameCount/30) * cos(i * 3 + (frameCount * timespeed) / 5) + height / 2;

    // hint: drive offsetX and offsetY with noise() instead of random()
    let offsetX =
      (noise(i * frequency * 2 + frameCount * timespeed, 0) - 0.5) *
      amplitude *
      100;
    let offsetY =
      (noise(i * frequency * 2 + frameCount * timespeed, 1000) - 0.5) *
      amplitude *
      100;
    dotSize = noise(i);
    fill(10, i * 5);

    ellipse(x + offsetX, y + offsetY, dotSize * 10, dotSize * 10);
  }
}

// require https://cdn.jsdelivr.net/npm/p5@latest/lib/p5.min.js

let cols;
let rows;
let current; 
let previous; 

let dampening = 0.99;
let rain = []
let dropAmount=50;

function setup() {
  pixelDensity(1);
  createCanvas(600, 400);
  cols = width;
  rows = height;

  current = new Array(cols).fill(0).map(n => new Array(rows).fill(0));
  previous = new Array(cols).fill(0).map(n => new Array(rows).fill(0));

  for(var i = 0; i < dropAmount; i++) {
      rain[i] = new rainDrop();
}
}

function mouseDragged() {
  previous[mouseX][mouseY] = 2500;
}

function draw() {
  background(0);


  if(frameCount%10==0){
    previous[int(random(width))][int(random(height))]=random(500,5000);
  }
  loadPixels();
  for (let i = 1; i < cols - 1; i++) {
    for (let j = 1; j < rows - 1; j++) {
      current[i][j] =
        (previous[i - 1][j] +
          previous[i + 1][j] +
          previous[i][j - 1] +
          previous[i][j + 1]) /
          2 -
        current[i][j];
      current[i][j] = current[i][j] * dampening;

      let index = (i + j * cols) * 4;
      pixels[index + 0] = current[i][j];
      pixels[index + 1] = current[i][j];
      pixels[index + 2] = current[i][j];
    }
  }
  updatePixels();

  let temp = previous;
  previous = current;
  current = temp;
    for(var i = 0; i < dropAmount; i++) {
    rain[i].show();
    rain[i].update();
}
}

function rainDrop() {
  this.x = random(0, width);
  this.y = random(0, -height);

  this.show = function() {
    noStroke();
    fill(255);
    ellipse(this.x, this.y, 2,5);   
  }
  this.update = function() {
    this.speed = 3;
    this.gravity = 1.05;
    this.y = this.y + this.speed*this.gravity;  

    if (this.y > height) {
      this.y = random(0, -height);
      this.gravity = 0;
}
}
}

