let wave;
let frequency;
let xspacing = 16; // Distance between each horizontal location
let w; // Width of entire wave
let theta = 0.0; // Start angle at 0
let amplitude; // Height of wave
let dx; // Value for incrementing x
let yvalues; // Using an array to store height values for the wave
let soundStart = true;

function setup() {
  frequency=random(100,400);
  createCanvas(800, 400);
  wave = new p5.Oscillator();
  wave.setType('sine');
  wave.amp(0.5);
  wave.freq(300);
  wave.start();
  slider = createSlider(0,100,50);
  slider.position(10,350);

  w = width + 16;
  yvalues = new Array(floor(w / xspacing));

}

function mousePressed() {
  userStartAudio();
  soundStart=false;
}

function draw() {
  background(0,0,map(frequency,100,400,0,255));
    dx = (TWO_PI / frequency) * xspacing;
    calcWave();
  renderWave();

  if(frameCount%120==0){
    frequency=random(100,400);
  }
  wave.freq(frequency);
  let m = map(slider.value(),0,100,0,1);
  amplitude=100*m;
  wave.amp(m);
  text("volume",50,380);
  if(soundStart==true){
    push();
    textAlign(CENTER);
    textSize(40);
    text("PRESS TO START SOUND",400,200);
    pop();
  }
}

function calcWave() {
  // Increment theta (try different values for
  // 'angular velocity' here)
  theta += 0.02;

  // For every x value, calculate a y value with sine function
  let x = theta;
  for (let i = 0; i < yvalues.length; i++) {
    yvalues[i] = sin(x) * amplitude;
    x += dx;
  }
}

function renderWave() {
  noStroke();
  fill(255);
  // A simple way to draw the wave with an ellipse at each location
  for (let x = 0; x < yvalues.length; x++) {
    ellipse(x * xspacing, height / 2 + yvalues[x], 16, 16);
  }
}

