// this gives the size of each block
const BLOCK_SIDE = 100;

// this gives a divisional element used for calculations within the DrawBlock function
const BLOCK_DIV = 10;

function setup() {
  createCanvas(800, 800);
}

function draw() {
  background("#022859");

  for (i = 0; i < width ; i+= BLOCK_SIDE){
    for (j = 0; j < height; j+= BLOCK_SIDE){
      DrawBlock(i,j);
    }
  }

}

function DrawBlock(x, y) {
  // not creating a function for each of the elements because they will repeat only 4 times, so listing 4 times instead of rotating each element

  // drawing the four ellipses in each block
  ellipse(x + BLOCK_SIDE / 4, y + BLOCK_SIDE / 4, BLOCK_SIDE / 2);
  ellipse(x + (BLOCK_SIDE * 3) / 4, y + BLOCK_SIDE / 4, BLOCK_SIDE / 2);
  ellipse(x + BLOCK_SIDE / 4, y + (BLOCK_SIDE * 3) / 4, BLOCK_SIDE / 2);
  ellipse(x + (BLOCK_SIDE * 3) / 4, y + (BLOCK_SIDE * 3) / 4, BLOCK_SIDE / 2);

  //drawing the diagonal lines that go across the circles
  strokeWeight(BLOCK_SIDE / 15);
  stroke("#022859");
  line(
    x + BLOCK_SIDE / BLOCK_DIV,
    y,
    x + BLOCK_SIDE - BLOCK_SIDE / BLOCK_DIV,
    y + BLOCK_SIDE
  );
  line(
    x,
    y + BLOCK_SIDE / BLOCK_DIV,
    x + BLOCK_SIDE,
    y + BLOCK_SIDE - BLOCK_SIDE / BLOCK_DIV
  );
  line(
    x + BLOCK_SIDE / BLOCK_DIV,
    y + BLOCK_SIDE,
    x + BLOCK_SIDE - BLOCK_SIDE / BLOCK_DIV,
    y
  );
  line(
    x,
    y + BLOCK_SIDE - BLOCK_SIDE / BLOCK_DIV,
    x + BLOCK_SIDE,
    y + BLOCK_SIDE / BLOCK_DIV
  );

  //drawing triangle that forms a connecting element between the blocks
  push();
  noStroke();
  triangle(
    x,
    y + BLOCK_SIDE / 2 - BLOCK_DIV,
    x,
    y + BLOCK_SIDE / 2 + BLOCK_DIV,
    x + BLOCK_DIV,
    y + BLOCK_SIDE / 2
  );

  triangle(
    x + BLOCK_SIDE,
    y + BLOCK_SIDE / 2 - BLOCK_DIV,
    x + BLOCK_SIDE,
    y + BLOCK_SIDE / 2 + BLOCK_DIV,
    x + BLOCK_SIDE - BLOCK_DIV,
    y + BLOCK_SIDE / 2
  );

  triangle(
    x + BLOCK_SIDE / 2 - BLOCK_DIV,
    y,
    x + BLOCK_SIDE / 2 + BLOCK_DIV,
    y,
    x + BLOCK_SIDE / 2,
    y + BLOCK_DIV
  );

  triangle(
    x + BLOCK_SIDE / 2 - BLOCK_DIV,
    y + BLOCK_SIDE ,
    x + BLOCK_SIDE / 2 + BLOCK_DIV,
    y + BLOCK_SIDE,
    x + BLOCK_SIDE / 2,
    y + BLOCK_SIDE -BLOCK_DIV
  );
  pop();
}

