function colorGradient() {
	for (var x = 0; x < width; x += PARAMS.step) {
		for (var y = 0; y < height; y += PARAMS.step) {
			let n =
				noise(
					(x / PARAMS.step + frameCount / 10) * 0.01,
					(y / PARAMS.step) * 0.02,
					frameCount / 30
				) * PARAMS.amplitude;

			let m = sin(x * 3 / PARAMS.step + n) * tan(y  / PARAMS.step + n) * 2;
            let o = atan((x / PARAMS.step) * 10 + n) - tan((y / PARAMS.step) * 30 + n) * 6;

			let c = colors[int(abs(n)) % colors.length];
			fill(c);
			rect(x, y, PARAMS.step);
		}
	}
}

