function DrawDiamond(x, y, length) {
  myTurtle.pushState();
  myTurtle.penUp();
  myTurtle.moveTo(x, y);

  myTurtle.penDown();

  myTurtle.turnTo(-65);

  myTurtle.moveForward(length);
  myTurtle.turnLeft(50);
  myTurtle.moveForward(length);
  myTurtle.turnLeft(130);
  myTurtle.moveForward(length);
  myTurtle.turnLeft(50);
  myTurtle.moveForward(length);

  myTurtle.penUp();
  myTurtle.popState();
}

function DrawTopWing(x, y, dir, startAngle, length) {
  myTurtle.pushState();
  myTurtle.penUp();
  myTurtle.moveTo(x, y);
  myTurtle.penDown();
  myTurtle.turnRight(dir * startAngle);
  myTurtle.moveForward(length * 1.4);
  myTurtle.turnLeft(dir * 15);
  myTurtle.moveForward(length);
  myTurtle.turnLeft(dir * 10);
  myTurtle.moveForward(length * 1.6);
