  myTurtle.moveForward(length * 1.6);
  myTurtle.turnLeft(dir * 30);
  myTurtle.moveForward(length * 0.6);
  myTurtle.turnLeft(dir * 35);
  myTurtle.moveForward(length * 1.2);
  myTurtle.turnLeft(dir * 10);
  myTurtle.moveForward(length * 1.4);

  myTurtle.popState();
}

function DrawBottomWing(x, y, dir, startAngle, length) {
  myTurtle.pushState();
