      let p = createVector(x,y);
      points.push(p)
    }
  }

  button1 = createButton('noise mode');
  button1.position(250, 400);
  button1.mouseClicked(noiseMode);
  button2 = createButton('random mode');
  button2.position(450, 400);
  button2.mouseClicked(randomMode);
}

function draw() {
  switch(mode){
    case 0:
    for(let i=0;i<points.length;i++){
      ellipse(points[i].x,points[i].y,2);
    }
      break;

    case 1:
      noiseSpread();
      break;

    case 2:
      randomSpread();
      break;
  }

  //noiseSpread();
}

function noiseSpread(){
    noStroke();
  fill(255);
  for(let i=0;i<points.length;i++){
    ellipse(points[i].x,points[i].y,1);
    var angle = map(noise(points[i].x,points[i].y),0,1,0,720);
    points[i].add(createVector(cos(angle),sin(angle)));
  }
}

function randomSpread(){
    noStroke();
  fill(255);
  for(let i=0;i<points.length;i++){
    ellipse(points[i].x,points[i].y,1);
    var angle = random(0,360);
    points[i].add(createVector(cos(angle),sin(angle)));
  }
}

function noiseMode(){
  mode=1;
  button1.hide();
  button2.hide();
}

function randomMode(){
  mode=2;
  button1.hide();
  button2.hide();
}

