//Set colors
let map = null;
let noiseScale = 1/180;
let ocean = "#008dc4";
let shore = "#00a9cc";
let sand = "#ffcef2";
let land = "#800080";
let stone = "#676767";
let snow = "#fffafa";


//Set up the canvas
function setup()
{
  createCanvas(500, 500);
  noStroke();
  noiseDetail(5, 0.5);
  makeMap();
  drawMap();
}

//Create a two layer array that assigns a color to each pixel
function makeMap()
{
  map = [];
  for(let i = 0; i < width; i++)
  {
    map[i] = [];
    for(let j = 0; j < height; j++)
    {
      map[i][j] = pickColor(i, j);
    }
  }
}

//pick color based on map location 
function pickColor(i, j)
{
  //declare a height value using noise function
  let h = noise((i)*noiseScale,
               (j)*noiseScale);

  if(h < 0.2)
  {
    c = ocean;
  } 
  else if(h < 0.3)
  {
    c = shore;

  }
  else if(h < 0.4)
  {
      c = sand;

  }
  else if(h < 0.5)
  {
      c = land;

  }
  else if(h < 0.6)
  {
      c = stone;
  }
  else if (h < 0.7)
  {
      c = snow;
  }
  else
  {
    c = snow;
  }

  return color(c);
}

function drawMap()
{
  for(let i = 0; i < width; i++)
  {
    for(let j = 0; j < height; j++)
    {
      set(i, j, map[i][j])//set(pixel x value, pixel y value, color value)
    }
  }
  updatePixels();
}


