//move the mouse to create different patterns with points in a sphere
function drawSphere(direction,hue) {
  rotateY(rotY);
  rotateX(rotX);
  rotX+=0.01;
  rotY+=0.02;
  orbitControl(10, 10);
  //different stroke options
  //stroke(150, 235, int(random(100, 255)));
  //stroke(int(random(100, 255)), 190, int(random(100, 255)));
  //stroke(int(random(255)),0,int(random(255)))
  switch(hue){
    case 0:
      stroke(0,int(random(255)),int(random(255)),random(255));
      break;
      case 1:
      stroke(int(random(255)),0,int(random(255)),random(255));
      break;
      case 2:
      stroke(int(random(255)),int(random(255)),0,random(255));
      break;
  }
  //stroke(0,int(random(255)),int(random(255)),random(255));

  for (let a = 0; a < 180; a += 25) {
    for (let b = 0; b < 90; b += 25) {
      beginShape(LINES);
      x = 200 * sin(a) * cos(b)*direction;
      y = 200 * cos(a);
      z = 200 * sin(a) * sin(b);
      vertex(x, y, z);
      vertex(x*sin(a),y,z);
      endShape();
    }
  }
}

