function drawBranch(length) {
  if (length < 5) {
    return;
  }
  strokeWeight(length / 50);

  t.moveForward(length * 0.25);
  t.turnLeft(10);
  t.moveForward(length * 0.25);
  t.turnRight(10);

  t.pushState();
  t.turnLeft(random(20, 40));
  drawBranch(length * 0.6);
  t.popState();

  t.pushState();
  t.turnRight(random(10, 30));
  drawBranch(length * 0.6);
  if (length < 30) {
    for (var i = 0; i < 20; i++) {
      t.moveForward(1);
      t.turnRight(6);
    }
    t.turnRight(80);
    for (var i = 0; i < 20; i++) {
      t.moveForward(1);
      t.turnRight(6);
    }
  }
  t.popState();
}

//draw flowers
function drawFlower(k, x, y, out_diam, in_diam){

    myTurtle.pushState();
    strokeWeight(2)
    stroke('#d55d73')
    myTurtle.popState();

    myTurtle.penUp();
    myTurtle.moveTo(x, y);
    myTurtle.penDown();


    for (let i = 0; i < k; i++){
        myTurtle.moveBackward(out_diam);
        myTurtle.turnLeft(4);
    }


    myTurtle.penUp();
    myTurtle.moveTo(x, y);
    myTurtle.penDown();


    for (let i = 0; i < k; i++){
        myTurtle.moveBackward(out_diam);
        myTurtle.turnRight(4);

        //myTurtle.moveForward(2);
    }


    myTurtle.pushState();
    stroke('#FAC16F');
    myTurtle.popState();

    myTurtle.penUp();
    myTurtle.moveTo(x, y);
    myTurtle.penDown();


    for (let i = 0; i < 20; i++){
        myTurtle.moveForward(in_diam);
        myTurtle.turnLeft(30);
    }

}

function drawTree(length) {
  if (length < 3) {
    return;
  }

  // draw this branch
  strokeWeight(length / 10);
  tu.moveForward(length * 0.9);
  tu.turnRight(10);
  tu.moveForward(length * 0.5);

  // left child
  tu.pushState();
  tu.turnLeft(random(20, 40));
  drawTree(length * 0.75);
  //drawBranch(length * random(.5, .9));

  tu.popState();

  // right child
  tu.pushState();
  tu.turnRight(random(10, 30));
  drawTree(length * 0.75);
  //drawBranch(length * random(.5, .9));
  tu.popState();
}

// require https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js

let amplitude_slider;
let frequency_slider;
let timespeed_slider

let startX = 50;
let startY = 250;
let endX = 450;
let endY = 50;
let ts=0;

function setup() {
  createCanvas(500, 300);

  createP("Frequency");
  frequency_slider = createSlider(0, 100, 20);
  createP("Amplitude");
  amplitude_slider = createSlider(0, 100, 50);
  createP("Time Speed");
  timespeed_slider = createSlider(0, 100, 50);
}

function draw() {
  background(50);
  ellipseMode(CENTER);

  let frequency = frequency_slider.value() / 100;
  let amplitude = amplitude_slider.value() / 100;
  let timespeed = timespeed_slider.value() / 100;

  ts+=timespeed*0.1

  noiseDetail(1, 0.5);

  fill(255);
  noStroke();


  // study this loop. do you understand how the line of ellipses is created?
  for (i = 0; i < 1; i += 0.02) {
    let x = lerp(startX, endX, i);
    let y = lerp(startY, endY, i);

    // the lerp() calculates a number between two numbers at a specific increment
    // i gives the value of interpolation between the two values of start and end. 
    // we use the lerp() for both the x and y coordinates 

    const n=i*frequency*50+ts

    // hint: drive offsetX and offsetY with noise() instead of random()
    let offsetX = (noise(n)-0.5) * amplitude * -80;
    let offsetY = (noise(n)-0.5) * amplitude * -80;

    ellipse(x + offsetX, y + offsetY, 10, 10);
  }
}

