    particles.push(new Particle(mouseX + 30, mouseY + 30));
  }
}

function keyPressed() {
  if (keyCode === ESCAPE) {
    flip = !flip;
  }
}

class Particle {
  constructor(x, y) {
    this.x = x;
    this.y = y;
    this.size = random(10, 100);
    this.offset = random(-50, 50);
  }

  //draw bubbles
  draw() {
    push();
    translate(this.x, this.y);
    let gradient = drawingContext.createLinearGradient(
      mouseX / 2 - 20,
      mouseY / 2 - 20,
      mouseX / 2 + 20,
      mouseY / 2 + 20
    );
    gradient.addColorStop(0, color(179, 234, 242, 150));
    gradient.addColorStop(1, color(211, 236, 240, 100));
    drawingContext.fillStyle = gradient;
    stroke("white");
    strokeWeight(1);
    ellipse(
      10 + this.offset,
      0 + this.offset,
      random(0.95, 1) * this.size,
      random(0.95, 1) * this.size
    );
    this.seed += 0.001;
    pop();
  }

  //update position
  update2() {
    this.x += random(-1, 1) * (1 + noise(this.x));
    this.y -= random(0, 2) * (5 + noise(this.y));
    if (this.y < 0) {
      return;
    }
  }
}

