}

function stippleRect(left, top, width, height, drawDensity) {
  drawCount = ((width * height) / 25) * drawDensity;
  for (i = 0; i < drawCount; i++) {
    let r= random(255);
    let g= random(255);
    let b= random(255);
    fill(r,g,b);
    let x = random(left, left + width);
    let y = random(top, top + height);
    ellipse(x, y, random (5), random(5));
  }
}

