const FRAME_LIMIT = 600;
const STAIR_NUM = 4;
const STAIRS = [];
let ball = {};
let sound = {};
let myBuffer;

function preload() {
	sound.hitWall = loadSound('./assets/hit.wav');
	sound.hitStairs = loadSound('./assets/game-ball-tap.wav');
}

function setup() {
	createCanvas(400, 400);
	respawn();

	for (let i = 0; i < STAIR_NUM; i++) {
		STAIRS[i] = new Stairs(i, 50, 90, 70, 10);
	}
	// console.log(STAIRS);
}

function draw() {
	background(20);
	noStroke();

	//setup
	if (frameCount % FRAME_LIMIT === 0) respawn(); // initialize the ball every 1000 frames

	//update
	changeVelocity(ball);
	soundFade(); // don't assign vol to the function

	if (isWall()) sound.hitWall.play();

	for (let i = 0; i < STAIR_NUM; i++) {
		if (STAIRS[i].isCollide(ball)) {
			//fill('yellow');
			sound.hitStairs.play();
		} else {
			//fill('red');
		}
	}

	// draw
	for (let i = 0; i < STAIR_NUM; i++) {
		STAIRS[i].draw();
	}
	ellipse(ball.x, ball.y, ball.r * 2);
}

function changeVelocity(ball) {
	ball.x += ball.dX;
	ball.y += ball.dY;
	ball.dY += 1.1;
}

function isWall() {
	// if hit the wall on the right
	if (ball.x > width - ball.r) {
		ball.dX = -abs(ball.dX) * 0.8;
		return true;
	}
	// if hit the wall on the left
	if (ball.x < ball.r) {
		ball.dX = abs(ball.dX) * 0.8;
		return true;
	}
	// if hit the ground
	if (ball.y > height - ball.r) {
		ball.dY = -abs(ball.dY) * 0.9;
		return true;
	}
	// if hit the ceiling
	if (ball.y < ball.r) {
		ball.dY = abs(ball.dY) * 0.9;
		return true;
	}
}

function respawn() {
	// set up initial values to the ball
	ball.x = 200;
	ball.y = 200;
	ball.r = 20;
	ball.dX = random(-10, 10);
	ball.dY = random(-10, -20);
	vol = 1;
}

/* ------------------------ Object ------------------------ */

class Stairs {
	constructor(i, _x, _y, _w, _h) {
		(this.x = _x + i * 70),
			(this.y = _y + i * 70),
			(this.w = _w),
			(this.h = _h);
	}

	draw() {
		push();
		fill(240);
		rect(this.x, this.y, this.w, this.h);
		pop();
	}

	isCollide(ball) {
		let border_left = this.x;
		let border_right = this.x + this.w;
		let border_top = this.y;
		let border_bottom = this.y + this.h;

		// if the ball is above the stair
		if (ball.y + ball.r > border_top && ball.y + ball.r < border_bottom) {
			if (ball.x + ball.r > border_left && ball.x + ball.r < border_right) {
				ball.dY = -abs(ball.dY) * 0.9;
				return true;
			} else {
				return false;
			}
		}
		// if the ball is below the stair
		if (ball.y - ball.r > border_top && ball.y - ball.r < border_bottom) {
			if (ball.x + ball.r > border_left && ball.x + ball.r < border_right) {
				ball.dY = abs(ball.dY) * 0.9;
				return true;
			} else {
				return false;
			}
		}
	}
}

/* ------------------------ Sound Effect ------------------------ */

function soundFade() {
	// sound fades every frame
	sound.hitWall.setVolume(vol);
	if (vol > 0) {
		vol -= 1 / FRAME_LIMIT;
	} else {
		vol = 0;
	}
}

function makeBuffer() {
	let index = 4100;
	const buffer = new Float32Array(index);
	// sin wave
	const wavelength = index / 440;
	for (let i = 0; i < index; i++) {
		buffer[i] = sin((i / wavelength) * 2 * PI);
	}
	myBuffer = new p5.SoundFile();
	myBuffer.setBuffer([buffer]);

	myBuffer.play(0, 1, 1);
}

