function nonCircle(locationX, locationY, radius, warp){

    // Draws a circle, changes its hue slightly, and moves its segments points by a random amount
    var doodlyCircle = new Path.Circle([locationX, locationY], radius);
    doodlyCircle.fillColor = "red";
    doodlyCircle.fillColor.hue += randColor - Math.random() * 150
    doodlyCircle.fillColor = "white";

    for (var i = 0; i < doodlyCircle.segments.length; i++){
        randOffset = Math.random() * warp - (warp);
        doodlyCircle.segments[i].point += randOffset
    }

    return doodlyCircle;
}

//Randomly places doodlyCircles around canvas
function randPlace(number){

    for (var i = 0; i < number; i++){
        var doodlyCircle = nonCircle(Math.random()*500, Math.random()*500, SIZE, WARP)
    }
    return doodlyCircle;
}

// Randomly decides on a direction for shape to move
function randMove(number){

    for (var i = 0; i < number; i++){
        var deltaPosition = new Point(1, 0);
        direction.push(deltaPosition);
    }

    for (var i = 0; i < number; i++){
        deltaPosition = new Point(0, 1);
        direction.push(deltaPosition);
    }    

    shuffle(direction);
    direction.length = number

    for (var i = 0; i < number; i++){
        if (direction[i].x === 1){
            directionName.push(0);
        } else directionName.push(1);
    }

    return direction, directionName;
}

// Randomly decides on a speed for an object to travel
function randSpeed(number){
    for (var i = 0; i < number; i++){
        speed.push(Math.random());
    }
    return speed;
}


