  angleMode(DEGREES);
  imageMode(CENTER);

  // shuffle the deck first
  values = shuffle(values);

  // pull as many values as we need
  for (let i = 0; i < 5; i++) {
    var v = values[i];
    append(selectedValues, v);
  }

  // shuffle the deck first
  selectedValues = shuffle(selectedValues);

  // pull as many values as we need
  for (let i = 0; i < 200; i++) {
    // find the value at the current position in the deck
    var v = selectedValues[index];

    // change the position for next time
    index++;

    // if that was the last value, shuffle and start over from the top
    if (index == selectedValues.length) {
      selectedValues = shuffle(selectedValues);
      index = 0;
    }

    randomTopping(v);
  }
  randomName();
}

