	link.download = fileName;
	link.href = url;
	link.click();
}


function radians(degrees) {
	return degrees * (Math.PI / 180);
}

function sin(a) {
	return Math.sin(radians(a));
}

function cos(a) {
	return Math.cos(radians(a));
}

